// ドキュメント情報を準備
const item = $input.first();
const binaryData = item.binary?.data;

if (!binaryData) {
  return [{
    json: {
      error: 'No binary data found',
      success: false
    }
  }];
}

const fileName = binaryData.fileName || item.json.name || 'unknown';
const mimeType = binaryData.mimeType || 'application/octet-stream';
const fileSize = binaryData.fileSize || 0;

// ファイル拡張子を取得
const ext = fileName.split('.').pop()?.toLowerCase() || '';

// MIMEタイプのマッピング
const mimeMap = {
  'pdf': 'application/pdf',
  'docx': 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'xlsx': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  'txt': 'text/plain',
  'md': 'text/markdown'
};

return [{
  json: {
    originalPath: item.json.path,
    fileName: fileName,
    extension: ext,
    mimeType: mimeMap[ext] || mimeType,
    fileSize: fileSize,
    base64Data: binaryData.data
  },
  binary: item.binary
}];

const item = $input.first();
const prevData = $('ドキュメント準備').first().json;
const difyResponse = item.json;

// ドキュメントIDを取得
const documentId = difyResponse.document?.id || null;
const batch = difyResponse.batch || null;

return [{
  json: {
    documentId: documentId,
    batch: batch,
    fileName: prevData.fileName,
    originalPath: prevData.originalPath,
    status: 'indexing'
  }
}];

const item = $input.first();
const prevData = $('ドキュメントID取得').first().json;

return [{
  json: {
    success: true,
    fileName: prevData.fileName,
    originalPath: prevData.originalPath,
    documentId: prevData.documentId,
    status: 'completed',
    processedAt: new Date().toISOString()
  }
}];

const item = $input.first();
const prevData = $('ドキュメントID取得').first().json;
const statusData = item.json;

// まだ処理中の場合
const status = statusData.data?.[0]?.indexing_status || 'unknown';

if (status === 'indexing' || status === 'parsing') {
  // 処理中 - 待機して再チェック（ループの代わりに警告を出力）
  return [{
    json: {
      success: true,
      fileName: prevData.fileName,
      originalPath: prevData.originalPath,
      documentId: prevData.documentId,
      status: 'processing',
      message: 'Document is still being processed. Check Dify console for status.',
      processedAt: new Date().toISOString()
    }
  }];
}

// エラーの場合
return [{
  json: {
    success: false,
    fileName: prevData.fileName,
    originalPath: prevData.originalPath,
    documentId: prevData.documentId,
    status: status,
    error: statusData.data?.[0]?.error || 'Unknown error',
    processedAt: new Date().toISOString()
  }
}];
