// EXIF情報を抽出するコード（JPEG/TIFF対応）
const item = $input.first();
const binaryData = item.binary?.data;
const prevJson = $('ワークフロー開始').first().json;

if (!binaryData) {
  return [{
    json: {
      ...prevJson,
      exif: { error: 'No binary data found', has_gps: false }
    }
  }];
}

// Base64データを取得
const binaryBuffer = await this.helpers.getBinaryDataBuffer(0, 'data');
const base64String = binaryBuffer.toString('base64');
const fileName = binaryData.fileName || prevJson.name || 'unknown.jpg';

// EXIF解析関数
function parseExif(buffer) {
  const result = {
    datetime: null,
    latitude: null,
    longitude: null,
    has_gps: false,
    camera_make: null,
    camera_model: null
  };

  try {
    // JPEGマーカーを確認
    if (buffer[0] !== 0xFF || buffer[1] !== 0xD8) {
      return result; // JPEGではない
    }

    let offset = 2;
    while (offset < buffer.length - 1) {
      if (buffer[offset] !== 0xFF) {
        offset++;
        continue;
      }

      const marker = buffer[offset + 1];

      // APP1マーカー（EXIF）
      if (marker === 0xE1) {
        const length = (buffer[offset + 2] << 8) | buffer[offset + 3];
        const exifData = buffer.slice(offset + 4, offset + 2 + length);

        // 'Exif\0\0'を確認
        if (exifData[0] === 0x45 && exifData[1] === 0x78 && 
            exifData[2] === 0x69 && exifData[3] === 0x66) {
          parseExifData(exifData.slice(6), result);
        }
        break;
      }

      // SOSマーカー（画像データ開始）に達したら終了
      if (marker === 0xDA) break;

      const segmentLength = (buffer[offset + 2] << 8) | buffer[offset + 3];
      offset += 2 + segmentLength;
    }
  } catch (e) {
    result.error = e.message;
  }

  return result;
}

function parseExifData(data, result) {
  // エンディアン判定
  const littleEndian = data[0] === 0x49; // 'II'

  function readUint16(pos) {
    return littleEndian 
      ? data[pos] | (data[pos + 1] << 8)
      : (data[pos] << 8) | data[pos + 1];
  }

  function readUint32(pos) {
    return littleEndian
      ? data[pos] | (data[pos + 1] << 8) | (data[pos + 2] << 16) | (data[pos + 3] << 24)
      : (data[pos] << 24) | (data[pos + 1] << 16) | (data[pos + 2] << 8) | data[pos + 3];
  }

  function readRational(pos) {
    const num = readUint32(pos);
    const den = readUint32(pos + 4);
    return den !== 0 ? num / den : 0;
  }

  function readString(pos, len) {
    let str = '';
    for (let i = 0; i < len && data[pos + i] !== 0; i++) {
      str += String.fromCharCode(data[pos + i]);
    }
    return str.trim();
  }

  function convertGPS(degrees, minutes, seconds, ref) {
    let decimal = degrees + minutes / 60 + seconds / 3600;
    if (ref === 'S' || ref === 'W') decimal = -decimal;
    return decimal;
  }

  // IFD0を解析
  const ifd0Offset = readUint32(4);
  let gpsIfdOffset = null;
  let exifIfdOffset = null;

  function parseIFD(ifdOffset, isGPS = false) {
    const numEntries = readUint16(ifdOffset);
    let gpsLatRef = 'N', gpsLonRef = 'E';
    let gpsLat = null, gpsLon = null;

    for (let i = 0; i < numEntries; i++) {
      const entryOffset = ifdOffset + 2 + i * 12;
      const tag = readUint16(entryOffset);
      const type = readUint16(entryOffset + 2);
      const count = readUint32(entryOffset + 4);
      let valueOffset = entryOffset + 8;

      // 値が4バイトを超える場合はオフセットを読む
      const typeSize = [0, 1, 1, 2, 4, 8, 1, 1, 2, 4, 8, 4, 8][type] || 1;
      if (count * typeSize > 4) {
        valueOffset = readUint32(entryOffset + 8);
      }

      if (isGPS) {
        // GPS IFD
        if (tag === 0x01) gpsLatRef = readString(valueOffset, 1); // GPSLatitudeRef
        if (tag === 0x03) gpsLonRef = readString(valueOffset, 1); // GPSLongitudeRef
        if (tag === 0x02) { // GPSLatitude
          gpsLat = [readRational(valueOffset), readRational(valueOffset + 8), readRational(valueOffset + 16)];
        }
        if (tag === 0x04) { // GPSLongitude
          gpsLon = [readRational(valueOffset), readRational(valueOffset + 8), readRational(valueOffset + 16)];
        }
      } else {
        // IFD0 / ExifIFD
        if (tag === 0x010F) result.camera_make = readString(valueOffset, count); // Make
        if (tag === 0x0110) result.camera_model = readString(valueOffset, count); // Model
        if (tag === 0x0132) result.datetime = readString(valueOffset, count); // DateTime
        if (tag === 0x9003) result.datetime = readString(valueOffset, count); // DateTimeOriginal (優先)
        if (tag === 0x8825) gpsIfdOffset = readUint32(valueOffset); // GPSInfoIFDPointer
        if (tag === 0x8769) exifIfdOffset = readUint32(valueOffset); // ExifIFDPointer
      }
    }

    if (isGPS && gpsLat && gpsLon) {
      result.latitude = convertGPS(gpsLat[0], gpsLat[1], gpsLat[2], gpsLatRef);
      result.longitude = convertGPS(gpsLon[0], gpsLon[1], gpsLon[2], gpsLonRef);
      result.has_gps = true;
    }
  }

  parseIFD(ifd0Offset);
  if (exifIfdOffset) parseIFD(exifIfdOffset);
  if (gpsIfdOffset) parseIFD(gpsIfdOffset, true);
}

// EXIF解析実行
const exifResult = parseExif(binaryBuffer);

const result = {
  json: {
    originalPath: prevJson.path,
    fileName: fileName,
    base64Data: base64String,
    mimeType: binaryData.mimeType || 'image/jpeg',
    fileSize: binaryData.fileSize,
    exif: exifResult
  }
};

return [result];

const item = $input.first();
const geoData = item.json;
const prevData = $('EXIF抽出').first().json;

// 住所をフォーマット
const address = geoData.address || {};
const parts = [];

if (address.country) parts.push(address.country);
if (address.state || address.province) parts.push(address.state || address.province);
if (address.city || address.town || address.village) {
  parts.push(address.city || address.town || address.village);
}
if (address.suburb || address.neighbourhood) {
  parts.push(address.suburb || address.neighbourhood);
}

const formattedAddress = parts.join(', ') || geoData.display_name || '住所不明';

return [{
  json: {
    ...prevData,
    location: {
      formatted: formattedAddress,
      full: geoData.display_name,
      raw: address
    }
  }
}];

const item = $input.first();

return [{
  json: {
    ...item.json,
    location: {
      formatted: '位置情報なし',
      full: null,
      raw: null
    }
  }
}];

// 画像のBase64データからGemini APIリクエストペイロードを構築
// 前のノードからデータを取得（住所フォーマット or 位置情報なし）
let prevData;
try {
  prevData = $('住所フォーマット').first().json;
} catch (e) {
  try {
    prevData = $('位置情報なし').first().json;
  } catch (e2) {
    prevData = $('EXIF抽出').first().json;
  }
}

const base64Data = prevData.base64Data;
const mimeType = prevData.mimeType || 'image/jpeg';

if (!base64Data) {
  throw new Error('No base64Data found for image');
}

// Gemini APIリクエストペイロードを構築
const requestPayload = {
  contents: [{
    parts: [
      {
        text: `この画像を詳細に分析し、検索用のメタデータを作成してください。以下の点を含めて記述してください：

1. 写っている物体（具体的に）
   - 建物がある場合、その種類（マンション、ビル、戸建て、オフィス、商業施設など）
   - 車両がある場合、種類（乗用車、トラック、バイクなど）
   - 人物がいる場合、人数と状況（ビジネス、カジュアル、イベントなど）

2. シチュエーション・場面
   - 食事、会議、工事現場、旅行、イベント、日常など
   - 料理がある場合、ジャンル（イタリアン、和食、中華、カフェなど）

3. 雰囲気や色味
   - 明るい、暗い、モダン、レトロ、ポップ、落ち着いた雰囲気など

4. 読み取れる文字情報
   - 看板、書籍、掲示板など読める文字があれば記述

5. 場所の推測
   - 屋内/屋外、都市/郊外/自然など

出力は日本語の自然な文章で、検索キーワード化しやすい形式でお願いします。`
      },
      {
        inline_data: {
          mime_type: mimeType,
          data: base64Data
        }
      }
    ]
  }],
  generationConfig: {
    temperature: 0.7,
    maxOutputTokens: 1024
  }
};

return [{
  json: {
    ...prevData,
    geminiPayload: requestPayload
  }
}];

const item = $input.first();
const geminiResponse = item.json;

// 前のノードからデータを取得（住所フォーマット or 位置情報なし）
let prevData;
try {
  prevData = $('住所フォーマット').first().json;
} catch (e) {
  try {
    prevData = $('位置情報なし').first().json;
  } catch (e2) {
    prevData = $('EXIF抽出').first().json;
  }
}

// Geminiレスポンスからキャプションを抽出
let caption = '';
try {
  caption = geminiResponse.candidates[0].content.parts[0].text || '';
} catch (e) {
  caption = 'キャプション生成エラー';
}

// Dify用ドキュメントテキストを構築
const parts = [];
parts.push(`■ファイル名: ${prevData.fileName}`);

if (prevData.exif && prevData.exif.datetime) {
  parts.push(`■撮影日時: ${prevData.exif.datetime}`);
}

if (prevData.location && prevData.location.formatted) {
  parts.push(`■撮影場所: ${prevData.location.formatted}`);
}

if (prevData.exif && prevData.exif.latitude && prevData.exif.longitude) {
  parts.push(`■座標: 緯度${prevData.exif.latitude}, 経度${prevData.exif.longitude}`);
}

if (prevData.exif && prevData.exif.camera_model) {
  const camera = prevData.exif.camera_make 
    ? `${prevData.exif.camera_make} ${prevData.exif.camera_model}`
    : prevData.exif.camera_model;
  parts.push(`■カメラ: ${camera}`);
}

parts.push('■画像内容の説明:');
parts.push(caption);

const documentText = parts.join('\n');

return [{
  json: {
    ...prevData,
    visionCaption: caption,
    documentText: documentText
  }
}];

const item = $input.first();
const prevData = $('ドキュメント構築').first().json;
const difyResponse = item.json;

return [{
  json: {
    success: true,
    fileName: prevData.fileName,
    originalPath: prevData.originalPath,
    documentId: difyResponse.document?.id || null,
    processedAt: new Date().toISOString()
  }
}];
