// EXIF情報を抽出するコード
const item = $input.first();
const binaryData = item.binary?.data;

if (!binaryData) {
  return [{
    json: {
      ...item.json,
      exif: { error: 'No binary data found', has_gps: false }
    }
  }];
}

// Base64データを取得
const base64Data = binaryData.data;
const fileName = binaryData.fileName || item.json.name || 'unknown.jpg';

// EXIFデータの簡易解析（実際の運用ではPythonスクリプトを呼び出し推奨）
// ここでは基本情報を返す
const result = {
  json: {
    originalPath: item.json.path,
    fileName: fileName,
    base64Data: base64Data,
    mimeType: binaryData.mimeType || 'image/jpeg',
    fileSize: binaryData.fileSize,
    exif: {
      datetime: null,
      latitude: null,
      longitude: null,
      has_gps: false,
      camera_make: null,
      camera_model: null
    }
  }
};

return [result];

const item = $input.first();
const geoData = item.json;
const prevData = $('EXIF抽出').first().json;

// 住所をフォーマット
const address = geoData.address || {};
const parts = [];

if (address.country) parts.push(address.country);
if (address.state || address.province) parts.push(address.state || address.province);
if (address.city || address.town || address.village) {
  parts.push(address.city || address.town || address.village);
}
if (address.suburb || address.neighbourhood) {
  parts.push(address.suburb || address.neighbourhood);
}

const formattedAddress = parts.join(', ') || geoData.display_name || '住所不明';

return [{
  json: {
    ...prevData,
    location: {
      formatted: formattedAddress,
      full: geoData.display_name,
      raw: address
    }
  }
}];

const item = $input.first();

return [{
  json: {
    ...item.json,
    location: {
      formatted: '位置情報なし',
      full: null,
      raw: null
    }
  }
}];

