// 繝峨く繝･繝｡繝ｳ繝域ュ蝣ｱ繧呈ｺ門ｙ
const item = $input.first();
const binaryData = item.binary?.data;

if (!binaryData) {
  return [{
    json: {
      error: 'No binary data found',
      success: false
    }
  }];
}

const fileName = binaryData.fileName || item.json.name || 'unknown';
const mimeType = binaryData.mimeType || 'application/octet-stream';
const fileSize = binaryData.fileSize || 0;

// 繝輔ぃ繧､繝ｫ諡｡蠑ｵ蟄舌ｒ蜿門ｾ予nconst ext = fileName.split('.').pop()?.toLowerCase() || '';

// MIME繧ｿ繧､繝励・繝槭ャ繝斐Φ繧ｰ
const mimeMap = {
  'pdf': 'application/pdf',
  'docx': 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'xlsx': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  'txt': 'text/plain',
  'md': 'text/markdown'
};

return [{
  json: {
    originalPath: item.json.path || '/watch/documents/test.pdf',
    fileName: fileName,
    extension: ext,
    mimeType: mimeMap[ext] || mimeType,
    fileSize: fileSize,
    base64Data: binaryData.data
  },
  binary: item.binary
}];

const item = $input.first();
const prevData = $('繝峨く繝･繝｡繝ｳ繝域ｺ門ｙ').first().json;
const difyResponse = item.json;

// 繝峨く繝･繝｡繝ｳ繝・D繧貞叙蠕予nconst documentId = difyResponse.document?.id || null;
const batch = difyResponse.batch || null;

return [{
  json: {
    documentId: documentId,
    batch: batch,
    fileName: prevData.fileName,
    originalPath: prevData.originalPath,
    status: 'indexing',
    difyResponse: difyResponse
  }
}];

const item = $input.first();

return [{
  json: {
    success: true,
    message: 'Document uploaded to Dify successfully',
    fileName: item.json.fileName,
    documentId: item.json.documentId,
    batch: item.json.batch,
    processedAt: new Date().toISOString()
  }
}];
